import React from "react";
import { useState, useEffect } from "react";
import {
  Row,
  Col,
  TimePicker,
  Button,
  Radio,
  Divider,
  Card,
  Checkbox,
  Select
} from "antd";
import moment from "moment";
import { Link } from "@reach/router";

export const StepTempo = () => {
  const { Option } = Select;
  const option = [
    { label: "Condizione temporale", value: "Condizione temporale" },
