import React from 'react';
import { useState } from 'react';
import { Row, Col, Input, DatePicker, Button, Radio, Divider } from 'antd';

export const StepTempo = () => {

    const [selectedOptions, setPlainOptions] = useState("");
    const option= [
        {label:"Codizione temporale", value: "Codizione temporale"},
        {label: "Sempre", value: "Sempre"}];

   const onChange1 = e => {
        console.log('radio1 checked', e.target.value);
        setPlainOptions(e.target.value);
    }

    return (

<div>
    <h1 style={{textAlign: 'center'}}>Nuova Campagna</h1>
    <Divider ></Divider>
    <div className="gutter-example">
    <Row gutter={16} >
      <Col span={6}>
        <div style={{border: 5}}><h1 style={{textAlign: 'center'}}>Step 1</h1></div>
      </Col>

      <Col className="gutter-row" span={6}>
      <div><h1 style={{textAlign: 'center'}}>Step 2</h1></div>
      </Col>
      <Col className="gutter-row" span={6}>
      <div><h1 style={{textAlign: 'center'}}>Step 3</h1></div>
      </Col>
      <Col className="gutter-row" span={6}>
      <div><h1 style={{textAlign: 'center'}}>Step 4</h1></div>
      </Col>
    </Row>
    <Row gutter={16}>
      <Col className="gutter-row" span={6} >
      <Divider ></Divider>
      <div><h1 style={{textAlign: 'center'}}>Step Tempo</h1></div>
      </Col> 
      </Row>
      </div>
        <div style={{paddingLeft:130}}>

      <Radio.Group options={option} onChange={onChange1} value={selectedOptions}></Radio.Group> 
        </div>
        <br>
        </br>






    </div>

    );
}
