import React, { useState, useEffect } from "react";
import {
  Row,
  Col,
  DatePicker,
  Button,
  Divider,
  Card,
  Select,
  Radio
} from "antd";
import moment from "moment";
import { Link } from "@reach/router";
import { StepActions } from "./StepActions";

export const StepUtente = () => {
  const dateFormat = "DD/MM/YYYY";
  const { Option } = Select;

  const option = [
    { label: "Negozio Favorito", value: "negozioFavorito" },
    { label: "Negozio Frequentato", value: "negozioFrequentato" }
  ];

  const [StepUtente, setStepUtente] = useState({
    sesso: "",
    data: "",
    fascia: "",
    stato: "",
    indirizzo: "",
    Domicilio: "",
    Residenza: "",
    Livello: "",
    Attività: "",
    Punti: "",
    PuntiMancanti: "",
    Attenzione: "",
    DataPassaggio: "",
    DataAttivazione: "",
    Tipologia: "",
    Classe: "",
    Sottoclasse: "",
    Prodotto: "",
    LivelloInteresse: "",
    FrequenzaAcquisto: "",
    FrequenzaOnline: "",
    FrequenzaNegozio: "",
    UltimoAcquisto: "",
    AccessoNegozio: "",
    AccessoOnline: "",
    FasciaImporto: "",
    Negozio: "",
    Nazione: "",
    TipologiaPOS: "",
    POS: ""
  });

  const handleChange = value => {
    console.log(`selected ${value}`);
  };

  return (
    <div>
      <h1 style={{ textAlign: "center" }}>Nuova Campagna</h1>
      <Divider></Divider>
      <div className="gutter-example">
        <Row gutter={16}>
          <Col span={6}>
            <div style={{ border: 5 }}>
              <h1 style={{ textAlign: "center" }}>Step 1</h1>
            </div>
          </Col>

          <Col className="gutter-row" span={6}>
            <div>
              <h1 style={{ textAlign: "center" }}>Step 2</h1>
            </div>
          </Col>
          <Col className="gutter-row" span={6}>
            <div>
              <h1 style={{ textAlign: "center" }}>Step 3</h1>
            </div>
          </Col>
          <Col className="gutter-row" span={6}>
            <div>
              <h1 style={{ textAlign: "center" }}>Step 4</h1>
            </div>
          </Col>
        </Row>
        <Divider></Divider>
        <Row style={{ height: 10 }} gutter={16}>
          <Col className="gutter-row" span={6}>
            <div>
              <p style={{ textAlign: "center", textSizeAdjust: 8 }}>
                Step Utente
              </p>
            </div>
          </Col>
        </Row>
        <Divider></Divider>
      </div>
      <div style={{ paddingLeft: 100 }}>
        <Card title="Dettaglio Utente" style={{ width: 1600 }}>
          <Row>
            <Col span={6}>Sessio</Col>
            <Col span={6}>Compleanno</Col>
            <Col span={6}>Fascia d'età</Col>
            <Col span={6}>Stato civile</Col>
          </Row>
          <div>
            <br></br>
            <Row>
              <Col span={6}>
                <Select
                  name="sesso"
                  defaultValue="M"
                  style={{ width: 160 }}
                  onChange={value => {
                    setStepUtente({
                      ...StepUtente,
                      sesso: value
                    });
                  }}
                >
                  <Option value="M">M</Option>
                  <Option value="F">F</Option>
                </Select>
              </Col>
              <Col span={6}>
                <DatePicker
                  name="data"
                  defaultValue={moment("03/02/20", dateFormat)}
                  format={dateFormat}
                  onChange={(date, dateString) => {
                    setStepUtente({
                      ...StepUtente,
                      data: date.valueOf()
                    });
                  }}
                />
              </Col>
              <Col span={6}>
                <Select
                  name="fascia"
                  defaultValue="25-35"
                  style={{ width: 160 }}
                  onChange={value => {
                    setStepUtente({
                      ...StepUtente,
                      fascia: value
                    });
                  }}
                >
                  <Option value="25-35">25-35</Option>
                  <Option value="36-46">36-46</Option>
                </Select>
              </Col>
              <Col span={6}>
                <Select
                  name="stato"
                  defaultValue="Nubile"
                  style={{ width: 160 }}
                  onChange={value => {
                    setStepUtente({
                      ...StepUtente,
                      stato: value
                    });
                  }}
                >
                  <Option value="Nubile">Nubile</Option>
                  <Option value="Celice">Celibe</Option>
                  <Option value="Sposato">Sposato</Option>
                  <Option value="Vedovo">Vedovo</Option>
                </Select>
              </Col>
            </Row>
          </div>
          <br></br>
          <p></p>
          <div>
            <Row>
              <Col span={6}>Tipologia Indirizzo</Col>
              <Col span={6}>Nazione</Col>
              <Col span={6}>Città</Col>
            </Row>
          </div>
          <br></br>
          <div>
            <Row>
              <Col span={6}>
                <Select
                  name="indirizzo"
                  defaultValue="Domicilio"
                  style={{ width: 160 }}
                  onChange={value => {
                    setStepUtente({
                      ...StepUtente,
                      indirizzo: value
                    });
                  }}
                >
                  <Option value="Domicilio">Domicilio</Option>
                  <Option value="Residenza">Residenza</Option>
                </Select>
              </Col>
              <Col span={6}>
                <Select
                  name="Domicilio"
                  defaultValue="Italia"
                  style={{ width: 160 }}
                  onChange={value => {
                    setStepUtente({
                      ...StepUtente,
                      Domicilio: value
                    });
                  }}
                >
                  <Option value="Italia">Italia</Option>
                  <Option value="Altro">Altro</Option>
                </Select>
              </Col>
              <Col span={6}>
                <Select
                  name="Residenza"
                  defaultValue="Firenze"
                  style={{ width: 160 }}
                  onChange={value => {
                    setStepUtente({
                      ...StepUtente,
                      Residenza: value
                    });
                  }}
                >
                  <Option value="Firenze">Firenze</Option>
                  <Option value="Altro">Altro</Option>
                </Select>
              </Col>
            </Row>
          </div>
        </Card>
        <br></br> <p></p>
        <div>
          <Card title="Fidelity" style={{ width: 1600 }}>
            <Row>
              <Col span={6}>Livello di Fidelity</Col>
              <Col span={6}>Attività Fidelity recente</Col>
            </Row>
            <div>
              <br></br>
              <Row>
                <Col span={6}>
                  <Select
                    name="Livello"
                    defaultValue="Alta"
                    style={{ width: 160 }}
                    onChange={value => {
                      setStepUtente({
                        ...StepUtente,
                        Livello: value
                      });
                    }}
                  >
                    <Option value="Alta">Alta</Option>
                    <Option value="Bassa">Bassa</Option>
                  </Select>
                </Col>
                <Col span={6}>
                  <Select
                    name="Attività"
                    defaultValue="Alta"
                    style={{ width: 160 }}
                    onChange={value => {
                      setStepUtente({
                        ...StepUtente,
                        Attività: value
                      });
                    }}
                  >
                    <Option value="Alta">Alta</Option>
                    <Option value="Bassa">Bassa</Option>
                  </Select>
                </Col>
              </Row>
            </div>
            <br></br>
            <p></p>
            <div>
              <Row>
                <Col span={6}>Fidelity Card Points</Col>
                <Col span={6}>Punti mancanti al livello successivo</Col>
                <Col span={6}>Attenzione</Col>
              </Row>
            </div>
            <br></br>
            <div>
              <Row>
                <Col span={6}>
                  <Select
                    name="Punti"
                    defaultValue="Meno di 10"
                    style={{ width: 160 }}
                    onChange={value => {
                      setStepUtente({
                        ...StepUtente,
                        Punti: value
                      });
                    }}
                  >
                    <Option value="Meno di">Meno di 10</Option>
                    <Option value="Più di">Più di 10</Option>
                  </Select>
                </Col>
                <Col span={6}>
                  <Select
                    name="PuntiMancanti"
                    defaultValue="Meno di 10"
                    style={{ width: 160 }}
                    onChange={value => {
                      setStepUtente({
                        ...StepUtente,
                        PuntiMancanti: value
                      });
                    }}
                  >
                    <Option value="Meno di">Meno di 10</Option>
                    <Option value="Più di">Più di 10</Option>
                  </Select>
                </Col>
                <Col span={6}>
                  <Select
                    name="Attenzione"
                    defaultValue="Alta"
                    style={{ width: 160 }}
                    onChange={value => {
                      setStepUtente({
                        ...StepUtente,
                        Attenzione: value
                      });
                    }}
                  >
                    <Option value="Alta">Alta</Option>
                    <Option value="Bassa">Bassa</Option>
                  </Select>
                </Col>
              </Row>
            </div>
            <br></br>
            <p></p>
            <div>
              <Row>
                <Col span={6}>Data attivazione Fidelity</Col>
                <Col span={6}>Data ultimo passaggio di livello</Col>
              </Row>
            </div>
            <br></br>
            <Row>
              <Col span={6}>
                <DatePicker
                  name="DataAttivazione"
                  defaultValue={moment("03/02/20", dateFormat)}
                  format={dateFormat}
                  onChange={(date, dateString) => {
                    setStepUtente({
                      ...StepUtente,
                      DataAttivazione: date.valueOf()
                    });
                  }}
                />
              </Col>
              <Col span={6}>
                <DatePicker
                  name="DataPassaggio"
                  defaultValue={moment("03/02/20", dateFormat)}
                  format={dateFormat}
                  onChange={(date, dateString) => {
                    setStepUtente({
                      ...StepUtente,
                      DataPassaggio: date.valueOf()
                    });
                  }}
                />
              </Col>
            </Row>
          </Card>
        </div>
        <br></br>
        <p></p>
        <div>
          <Card title="Touch Point Prodotti" style={{ width: 1600 }}>
            <Row>
              <Col span={4}>Tipologia Merceologica</Col>
              <Col span={4}>Classe Merceologica</Col>
              <Col span={4}>Sottoclasse Merceologica</Col>
              <Col span={4}>Prodotto</Col>
              <Col span={4}>Livello di Interesse</Col>
            </Row>
            <br></br>
            <div>
              <Row>
                <Col span={4}>
                  <Select
                    name="Tipologia"
                    defaultValue="ComboBox"
                    style={{ width: 160 }}
                    onChange={value => {
                      setStepUtente({
                        ...StepUtente,
                        Tipologia: value
                      });
                    }}
                  >
                    <Option value="ComboBox">ComboBox</Option>
                    <Option value="Altro">Altro</Option>
                  </Select>
                </Col>
                <Col span={4}>
                  <Select
                    name="Classe"
                    defaultValue="ComboBox"
                    style={{ width: 160 }}
                    onChange={value => {
                      setStepUtente({
                        ...StepUtente,
                        Classe: value
                      });
                    }}
                  >
                    <Option value="ComboBox">ComboBox</Option>
                    <Option value="Altro">Altro</Option>
                  </Select>
                </Col>
                <Col span={4}>
                  <Select
                    name="Sottoclasse"
                    defaultValue="ComboBox"
                    style={{ width: 160 }}
                    onChange={value => {
                      setStepUtente({
                        ...StepUtente,
                        Sottoclasse: value
                      });
                    }}
                  >
                    <Option value="ComboBox">ComboBox</Option>
                    <Option value="Altro">Altro</Option>
                  </Select>
                </Col>
                <Col span={4}>
                  <Select
                    name="Prodotto"
                    defaultValue="ComboBox"
                    style={{ width: 160 }}
                    onChange={value => {
                      setStepUtente({
                        ...StepUtente,
                        Prodotto: value
                      });
                    }}
                  >
                    <Option value="ComboBox">ComboBox</Option>
                    <Option value="Altro">Altro</Option>
                  </Select>
                </Col>
                <Col span={4}>
                  <Select
                    name="LivelloInteresse<"
                    defaultValue="l1"
                    style={{ width: 160 }}
                    onChange={value => {
                      setStepUtente({
                        ...StepUtente,
                        LivelloInteresse: value
                      });
                    }}
                  >
                    <Option value="l1">l1</Option>
                    <Option value="l2">l2</Option>
                  </Select>
                </Col>
                <Col>
                  <Button>Rimuovi</Button> <Button>Aggiungi</Button>
                </Col>
              </Row>
            </div>
          </Card>
        </div>
        <br></br>
        <p></p>
        <div>
          <Card title="Analisi e Frequenza" style={{ width: 1600 }}>
            <Row>
              <Col span={6}>Frequenza media acquisto cliente</Col>
              <Col span={6}>Frequenza media accesso online</Col>
              <Col span={6}>Frequenza media accesso in negozio</Col>
            </Row>
            <br></br>

            <div>
              <Row>
                <Col span={6}>
                  <Select
                    name="FrequenzaAcquisto"
                    defaultValue="ComboBox"
                    style={{ width: 160 }}
                    onChange={value => {
                      setStepUtente({
                        ...StepUtente,
                        FrequenzaAcquisto: value
                      });
                    }}
                  >
                    <Option value="ComboBox">ComboBox</Option>
                    <Option value="Altro">Altro</Option>
                  </Select>
                </Col>
                <Col span={6}>
                  <Select
                    name="FrequenzaOnline"
                    defaultValue="ComboBox"
                    style={{ width: 160 }}
                    onChange={value => {
                      setStepUtente({
                        ...StepUtente,
                        FrequenzaOnline: value
                      });
                    }}
                  >
                    <Option value="ComboBox">ComboBox</Option>
                    <Option value="Altro">Altro</Option>
                  </Select>
                </Col>
                <Col span={6}>
                  <Select
                    name="FrequenzaNegozio"
                    defaultValue="ComboBox"
                    style={{ width: 160 }}
                    onChange={value => {
                      setStepUtente({
                        ...StepActions,
                        FrequenzaNegozio: value
                      });
                    }}
                  >
                    <Option value="ComboBox">ComboBox</Option>
                    <Option value="Altro">Altro</Option>
                  </Select>
                </Col>
              </Row>
            </div>
            <br></br>
            <p></p>
            <div>
              <Row>
                <Col span={6}>Giorni da ultimo acquisto</Col>
                <Col span={6}>Giorno da ultimo accesso in negozio</Col>
                <Col span={6}>Giorni da ultimo accesso online</Col>
              </Row>
              <br></br>

              <div>
                <Row>
                  <Col span={6}>
                    <Select
                      name="UltimoAcquisto"
                      defaultValue="ComboBox"
                      style={{ width: 160 }}
                      onChange={value => {
                        setStepUtente({
                          ...StepUtente,
                          UltimoAcquisto: value
                        });
                      }}
                    >
                      <Option value="ComboBox">ComboBox</Option>
                      <Option value="Altro">Altro</Option>
                    </Select>
                  </Col>
                  <Col span={6}>
                    <Select
                      name="AccessoNegozio"
                      defaultValue="ComboBox"
                      style={{ width: 160 }}
                      onChange={value => {
                        setStepUtente({
                          ...StepUtente,
                          AccessoNegozio: value
                        });
                      }}
                    >
                      <Option value="ComboBox">ComboBox</Option>
                      <Option value="Altro">Altro</Option>
                    </Select>
                  </Col>
                  <Col span={6}>
                    <Select
                      name="AccessoOnline"
                      defaultValue="ComboBox"
                      style={{ width: 160 }}
                      onChange={value => {
                        setStepUtente({
                          ...StepUtente,
                          AccessoOnline: value
                        });
                      }}
                    >
                      <Option value="ComboBox">ComboBox</Option>
                      <Option value="Altro">Altro</Option>
                    </Select>
                  </Col>
                </Row>
              </div>
            </div>
            <div>
              <br></br>
              <p></p>
              <Row>
                <Col span={6}>Fascia importo acquisto medio</Col>
              </Row>
              <br></br>

              <div>
                <Row>
                  <Col span={6}>
                    <Select
                      name="FasciaImporto"
                      defaultValue="ComboBox"
                      style={{ width: 160 }}
                      onChange={value => {
                        setStepUtente({
                          ...StepUtente,
                          FasciaImporto: value
                        });
                      }}
                    >
                      <Option value="ComboBox">ComboBox</Option>
                      <Option value="Altro">Altro</Option>
                    </Select>
                  </Col>
                </Row>
              </div>
            </div>
          </Card>
          <div>
            <Card title="Negozio Favorito" style={{ width: 1600 }}>
              <div>
                <Row>
                  <Col span={6}>
                    <Radio.Group
                      name="Negozio"
                      options={option}
                      onChange={value => {
                        setStepUtente({
                          ...StepUtente,
                          Negozio: value.target.value
                        });
                      }}
                      value={StepUtente.Negozio}
                    ></Radio.Group>
                  </Col>
                </Row>
                <br></br>
                <p></p>
                <div>
                  <Row>
                    <Col span={6}>Nazione</Col>
                    <Col span={6}>Tipologia POS</Col>
                    <Col span={6}>POS</Col>
                  </Row>
                </div>
                <br></br>
                <div>
                  <Row>
                    <Col span={6}>
                      <Select
                        name="Nazione"
                        defaultValue="Italia"
                        style={{ width: 160 }}
                        onChange={value => {
                          setStepUtente({
                            ...StepUtente,
                            Nazione: value
                          });
                        }}
                      >
                        <Option value="Italia">Italia</Option>
                        <Option value="Altro">Altro</Option>
                      </Select>
                    </Col>
                    <Col span={6}>
                      <Select
                        name="TipologiaPOS"
                        defaultValue="Boutique"
                        style={{ width: 160 }}
                        onChange={value => {
                          setStepUtente({
                            ...StepUtente,
                            TipologiaPOS: value
                          });
                        }}
                      >
                        <Option value="Boutique">Boutique</Option>
                        <Option value="Altro">Altro</Option>
                      </Select>
                    </Col>
                    <Col span={6}>
                      <Select
                        name="POS"
                        defaultValue="sanGiovanni"
                        style={{ width: 160 }}
                        onChange={value => {
                          setStepUtente({
                            ...StepUtente,
                            POS: value
                          });
                        }}
                      >
                        <Option value="sanGiovanni">
                          Piazza San Giovanni, 17
                        </Option>
                        <Option value="Altro"> Altro</Option>
                      </Select>
                    </Col>
                  </Row>
                </div>
                <br></br>
                <p></p>
                <Row>
                  <Col span={6}>Condizione Temporale</Col>
                  <Col span={6}>Tipologia POS</Col>
                </Row>
              </div>
              <br></br>
              <div>
                <Row>
                  <Col span={6}>
                    <Select
                      defaultValue="ComboBox"
                      style={{ width: 160 }}
                      onChange={handleChange}
                    >
                      <Option value="ComboBox">ComboBox</Option>
                      <Option value="Altro">Altro</Option>
                    </Select>
                  </Col>
                  <Col span={6}>
                    <Select
                      defaultValue="Camerino"
                      style={{ width: 160 }}
                      onChange={handleChange}
                    >
                      <Option value="Camerino">Camerino</Option>
                      <Option value="Altro">Altro</Option>
                    </Select>
                  </Col>
                </Row>
              </div>
            </Card>
          </div>
          <br></br>
          <p></p>

          <div style={{ paddingLeft: 100 }}>
            <Row>
              <Col span={6}>
                <Link to="/StepLocation">
                  <Button>Indietro</Button>
                </Link>
              </Col>
              <Col span={6}></Col>
              <Col span={6}></Col>

              <Col span={6}>
                <Link to="/StepActions">
                  <Button>Avanti</Button>
                </Link>
              </Col>
            </Row>
          </div>
        </div>
      </div>
    </div>
  );
};
