import React from 'react';
import logo from './logo.svg';
import 'antd/dist/antd.css';
import './App.css';
import { Select } from 'antd';
import { Table, Divider, Tag } from 'antd';

const { Column, ColumnGroup } = Table;

const { Option } = Select;


function App() {

  function handleChange(value) {
    console.log(`selected ${value}`);
  }

  return (
    <div className="App" style={{ marginTop: 100, marginLeft:200}}>
      <header className="App-header">
        <p style={{textAlign: 'left'}}>Elenco Campagne</p>
        <div style= {{ background: 'grey'}}>
            <Select 
              defaultValue="Tutte le campagne" 
              style={{ width: 1000}} 
              onChange={handleChange}>
              <Option value="Campagna 1">Campagna 1</Option>
              <Option value="Campagna 2">Campagna 2</Option>
              <Option value="Campagna 3">Campagna 3</Option>
            </Select>
            <Table style={{ width: 1000, marginLeft:75, marginTop: 110}} >
               <Column title="First Name" dataIndex="firstName" key="firstName" />
               <Column title="Last Name" dataIndex="lastName" key="lastName" />
               <Column title="Age" dataIndex="age" key="age" />
               <Column title="Address" dataIndex="address" key="address" />
               <Column
                    title="Tags"
                    dataIndex="tags"
                    key="tags"
                    render={tags => (
                      <span>
                        {tags.map(tag => (
                          <Tag color="blue" key={tag}>
                            {tag}
                          </Tag>
                        ))}
                      </span>
                    )}
                  />
            </Table>
            </div>
      </header>
    </div>
  );
}

export default App;
