import React, { useState, useRef } from "react";
import { Row, Col, Input, DatePicker, Button, Divider } from "antd";
import moment from "moment";
import { Select } from "antd";
import { Link } from "@reach/router";

export const NuovaCampagna = () => {
  const dateFormatList = ["DD/MM/YYYY", "DD/MM/YY"];
  const { Option } = Select;
  const { TextArea } = Input;
  const [step1, setStep1] = useState({
    nomeCampagna: "",
    dataInizio: "",
    dataFine: ""
  });
  console.log(step1);

  function handleChange(value) {
    console.log(`selected ${value}`);
    localStorage.setItem("Valore selezionato: " + value);
  }

  const onChangeInput = event => {
    console.log(event.target.name);
    setStep1({ ...step1, [event.target.name]: event.target.value });
  };

  const onChange = () => {
    localStorage.setItem("Step1", step1);
  };

  return (
    <div style={{ paddingLeft: 80, paddingTop: 50, paddingRight: 80 }}>
      <h1 style={{ textAlign: "center" }}>Nuova Campagna</h1>
      <Divider></Divider>
      <div className="gutter-example">
        <Row gutter={16}>
          <Col span={6}>
            <div style={{ border: 5 }}>
              <h1 style={{ textAlign: "center" }}>Step 1</h1>
            </div>
          </Col>

          <Col className="gutter-row" span={6}>
            <div>
              <h1 style={{ textAlign: "center" }}>Step 2</h1>
            </div>
          </Col>
          <Col className="gutter-row" span={6}>
            <div>
              <h1 style={{ textAlign: "center" }}>Step 3</h1>
            </div>
          </Col>
          <Col className="gutter-row" span={6}>
            <div>
              <h1 style={{ textAlign: "center" }}>Step 4</h1>
            </div>
          </Col>
        </Row>
        <Divider></Divider>
        <Row style={{ height: 10 }} gutter={16}>
          <Col className="gutter-row" span={6}>
            <div>
              <p style={{ textAlign: "center", textSizeAdjust: 8 }}>
                Nuova Campagna
              </p>
            </div>
          </Col>
        </Row>
        <Divider></Divider>
      </div>
      <br></br>
      <Row>
        <Col span={6}>Nome Campagna</Col>
        <Col span={6}>
          <Input name="nomeCampagna" placeholder="" onChange={onChangeInput} />
        </Col>
      </Row>
      <br></br>
      <div>
        <Row>
          <Col span={6}>Data Inizio Campagna</Col>
          <Col span={6}>
            <DatePicker
              defaultValue={moment("04/02/2020", dateFormatList[0])}
              name="dataInizio"
              format={dateFormatList}
              onChange={(date, dateString) => {
                setStep1({ ...step1, dataInizio: date.valueOf() });
              }}
            />
          </Col>
        </Row>
      </div>
      <br></br>
      <div>
        <Row>
          <Col span={6}>Data Fine Campagna</Col>
          <Col span={6}>
            <DatePicker
              defaultValue={moment("04/02/2020", dateFormatList[0])}
              name="dataFine"
              format={dateFormatList}
              onChange={(date, dateString) => {
                setStep1({ ...step1, dataInizio: date.valueOf() });
              }}
            />
          </Col>
        </Row>
      </div>
      <br></br>
      <div>
        <Row>
          <Col span={6}>Profilo Campagna</Col>
          <Col span={6}>
            <Select
              defaultValue="Global"
              style={{ width: 160 }}
              onChange={handleChange}
            >
              <Option value="Global">Global Campain</Option>
              <Option value="Local">Local Campain</Option>
            </Select>
          </Col>
        </Row>
      </div>
      <br></br>
      <div>
        <Row>
          <Col span={6}>Note</Col>
          <Col span={6}>
            <TextArea rows={2} style={{ paddingRight: 100 }} />
          </Col>
        </Row>
      </div>
      <div>
        <br></br>
        <p></p>

        <div>
          <Row>
            <Col span={6}></Col>
            <Col span={6}></Col>
            <Col span={6}></Col>

            <Col span={6}>
              <Link to="/StepTempo">
                <Button onClick={onChange}>Avanti</Button>
              </Link>
            </Col>
          </Row>
        </div>
      </div>
    </div>
  );
};
